<?phpinclude_once('application/controllers/Controller.php');include_once('application/models/M_News.php');include_once('application/models/M_Users.php');include_once('application/controllers/C_Base.php');class C_News extends C_Base{	public $news;	// Конструктор	function __construct() {				$this->news = array();	}		//Обробник запиту	protected function OnInput()	{		parent::OnInput();		$mNews = M_News::Instance();		$this->news = $mNews->GetAllNews();	}		protected function OnOutput() {      $vars = array('news' => $this->news, 'user' => $this->user);      $this->content = $this->View('templates/theme/v_news.php', $vars);      $this->title = 'Новини';      parent::OnOutput();	}	}