<?php //Базовый класс контролераabstract class Controller{	// Конструктор	function __construct()	{			}		// Повна обробка HTTP-запиту	public function Request()	{		$this->OnInput();		$this->OnOutput();	}		// Обробник запиту	protected function OnInput()	{	}		// Генератор HTML	protected function OnOutput()	{	}		// Запит проведений методом GET?	protected function IsGet()	{		return $_SERVER['REQUEST_METHOD'] == 'GET';	}	// Запит проведений методом POST?	protected function IsPost()	{		return $_SERVER['REQUEST_METHOD'] == 'POST';	}	// Генерація HTML шаблону в строку	protected function View($fileName, $vars = array())	{		// Встановлення змінних для шаблону		foreach ($vars as $k => $v)		{			$$k = $v;		}		// Генерація HTML в строку.		ob_start();		include $fileName;		return ob_get_clean();		}	}