<?phpinclude_once('application/controllers/Controller.php');include_once('application/models/M_Orders.php');include_once('application/controllers/C_Base.php');//Контролер звітів (список)class C_Orders extends C_Base{	public $orders;			// Конструктор	function __construct() {		$this->orders = array();	}		// Обробник запиту	protected function OnInput()	{		parent::OnInput();		$mOrder = M_Orders::Instance();		$this->orders = $mOrder->GetAllOrders();	}			protected function OnOutput() {      $vars = array('orders' => $this->orders);      $this->content = $this->View('templates/theme/v_orders.php', $vars);      $this->title = 'Бланки звітності';      parent::OnOutput();	}	}