<?phpinclude_once('application/models/M_Users.php');include_once('application/controllers/C_Base.php');//Конттролер головної сторінкиclass C_Users_Main extends C_Base{	private $text;		// Конструктор	function __construct() {		$this->text = '';	}		// Обробник запиту	protected function OnInput() {		parent::OnInput();		$mUsers = M_Users::Instance();		if($mUsers->Get()){			$this->text = '';		}	}		// Генератор HTML		protected function OnOutput() {		$vars = array('text' => $this->text);			$this->content = $this->View('templates/theme/v_main.php', $vars);		$this->scripts = '<!-- link to google maps plugin -->    <script type="text/javascript" src="https://maps.googleapis.com/maps/api/js?sensor=false"></script>    <script type="text/javascript">    <!-- init in page -->    initGoogleMaps();    <!-- javascript function -->        function initGoogleMaps(){        var myLatlng = new google.maps.LatLng(51.328207, 28.799175);        var mapOptions = {          zoom: 14,          center: myLatlng,          scrollwheel: false, //we disable de scroll over the map, it is a really annoing when you scroll through page        }        var map = new google.maps.Map(document.getElementById("map"), mapOptions);        var marker = new google.maps.Marker({            position: myLatlng,            title:"Hello World!"        });        // To add the marker to the map, call setMap();        marker.setMap(map);    }    </script>';      parent::OnOutput();	}	}