<?phpinclude_once('application/models/M_Users.php');include_once('application/controllers/C_Base.php');// Контролер сторінки профіля користувачаclass C_Users_Profile extends C_Base{	// Конструктор	function __construct()	{		$this->needLogin = true;		}		// Обробник запиту	protected function OnInput()	{		parent::OnInput();				$mUsers = M_Users::Instance();			$this->user['fam'] = ucfirst($this->user['fam']);		$this->user['name'] = ucfirst($this->user['name']);		$this->user['otch'] = ucfirst($this->user['otch']);		$this->user['login'] = ucfirst($this->user['login']);		$this->title = 'Профіль користувача'. ' - ' .$this->user['login'];		}		// Генератор HTML	protected function OnOutput()	{		$vars = array('user' => $this->user);		$this->content = $this->View('templates/theme/v_profile.php', $vars);		parent::OnOutput();	}		}